<?php/** * Created by PhpStorm. * User: Yann * Date: 21/02/2016 * Time: 17:25 */namespace MCBundle\Controller;use MCBundle\Entity\Film;use Symfony\Bundle\FrameworkBundle\Controller\Controller;use FOS\RestBundle\Controller\Annotations\View;use Sensio\Bundle\FrameworkExtraBundle\Configuration\ParamConverter;class MCRestController extends Controller{    /**     * @return array     * @View()     */    public function getFilmsAction()    {        $em = $this->getDoctrine()->getManager();        $films = $em->getRepository('MCBundle:Film')->findAll();        return array('films' => $films);    }    /**     * @param Film $film     * @return array     * @View()     * @ParamConverter("film", class="MCBundle:Film")     */    public function getFilmAction(Film $film)    {        return array('film' => $film);    }        public function addFilmAction(){            }}