<?phpnamespace MCBundle\Controller;use Symfony\Bundle\FrameworkBundle\Controller\Controller;use Symfony\Component\HttpFoundation\Response;use Symfony\Component\HttpFoundation\Request;//use Symfony\Component\HttpKernel\Exception\NotFoundHttpException;class MatiesChillController extends Controller{    public function indexAction()    {        return $this->render('MCBundle:Pages:index.html.twig');    }    public function sessionAction()    {        return $this->render('MCBundle:Pages:sessions.html.twig');    }    public function filmAction()    {        $data = array();        $allohelper = $this->get("mc_allo_helper");        $motsCles = "The Dark Knight";        $page = 1;        try {            $data = $allohelper->search($motsCles, $page);//            var_dump($data);        }// En cas d'erreur.        catch (\ErrorException $e) {            // Affichage des informations sur la requête            $error = "<pre>" . print_r($allohelper->getRequestInfos(), 1) . " </pre><br>";            // Afficher un message d'erreur.            $error .= "Erreur " . $e->getCode() . ": " . $e->getMessage();            return new Response($error);        }        return $this->render(            'MCBundle:Pages:films.html.twig',            array(                "motsCles" => $motsCles,                "movie" => $data["movie"]            )        );    }    /**     * @param Request $request     * @return Response     */    public function searchFilmAction($keyword)    {        $data = array();//        if ($request->isXmlHttpRequest()) {            //$em = $this->getDoctrine()->getManager();//            $keyword = $request->get('keyword');                        $allohelper = $this->get("mc_allo_helper");            $page = 1;            try {                $result = $allohelper->search($keyword, $page);                $data= $result["movie"];//            var_dump($data);            }// En cas d'erreur.            catch (\ErrorException $e) {                // Affichage des informations sur la requête                $error = "<pre>" . print_r($allohelper->getRequestInfos(), 1) . " </pre><br>";                // Afficher un message d'erreur.                $error .= "Erreur " . $e->getCode() . ": " . $e->getMessage();                return new Response($error);            }//        }        return $this->render(            'MCBundle:Pages:films.html.twig',            array(                "motsCles" => $keyword,                "movie" => $data            )        );    }    public function profilAction()    {        return $this->render('MCBundle:Pages:index.html.twig');    }}