<?php/** * Created by PhpStorm. * User: Yann * Date: 21/02/2016 * Time: 17:25 */namespace MCBundle\Controller\Rest;use MCBundle\Entity\Film;use Symfony\Bundle\FrameworkBundle\Controller\Controller;use FOS\RestBundle\View\View as FOSView;use Nelmio\ApiDocBundle\Annotation\ApiDoc;use FOS\RestBundle\Controller\Annotations\View;use FOS\RestBundle\Controller\Annotations\Get;use FOS\RestBundle\Controller\Annotations\Delete;use Sensio\Bundle\FrameworkExtraBundle\Configuration\ParamConverter;class MCRestFilmController extends Controller{    /**     * @Get("/films")     * @ApiDoc(     *   resource = true,     *   description = "Return the overall Film List",     *   statusCodes = {     *     200 = "Returned when successful",     *     404 = "Returned when the user is not found"     *   }     * )     * @return array     * @View()     */    public function getFilmsAction()    {        $em = $this->getDoctrine()->getManager();        $films = $em->getRepository('MCBundle:Film')->findAll();        $view = FOSView::create();        if ($films) {            $view->setData($films)->setStatusCode(200);        } else {            $view->setData($films)->setStatusCode(404);        }        return $view;    }    /**     * Get a Film.     * @Get("/users/{id}")     * @ApiDoc(     *   resource = true,     *   description = "Return an Film identified by ID",     *   statusCodes = {     *     200 = "Returned when successful",     *     404 = "Returned when the user is not found"     *   }     * )     *     * @param Film $film     * @return array     * @View()     * @ParamConverter("film", class="MCBundle:Film")     */    public function getFilmAction(Film $film)    {        return $film;    }    /**     * * Delete a Film identified by ID.     * @ApiDoc(     *   resource = true,     *   description = "Delete an Film identified by ID",     *   statusCodes = {     *     200 = "Returned when successful",     *     404 = "Returned when the user is not found"     *   }     * )     * @Delete("/films/delete/{id}")     * @param int $id the Film id     * @return View     */    public function removeFilmAction($id)    {        $em = $this->getDoctrine()->getManager();        $film = $em->getRepository('MCBundle:Film')->find($id);        $em->remove($film);        $em->flush();        $result = $em->getRepository('MCBundle:Film')->find($id);        $view = FOSView::create();        if (null == $result) {            $view->setData('Film deleted')->setStatusCode(200);        } else {            $view->setData('Film don\'t delete')->setStatusCode(404);        }        return $view;    }    /**     * @ApiDoc(     *   resource = true,     *   description = "Return the overall film List by Genre",     *   statusCodes = {     *     200 = "Returned when successful",     *     404 = "Returned when the user is not found"     *   }     * )     * @Get("/films/genre/{id_genre}")     * @param $id_genre     * @return View     */    public function getFilmByGenreAction($id_genre)    {        $em = $this->getDoctrine()->getManager();        $films = $em->getRepository('MCBundle:Film')->getByGenre($id_genre);                if (!$films) {            throw $this->createNotFoundException('Data not found.');        }        return $films;    }}